import React, { useEffect, useState } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { useSnackbar } from 'notistack';
import { useForm } from 'react-hook-form';
import ReactGA from 'react-ga4';

//  mui
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import MenuItem from '@mui/material/MenuItem';
import Stack from '@mui/material/Stack';
import { styled } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import { useTheme } from '@emotion/react';
import LoadingButton from '@mui/lab/LoadingButton';
import { KeyboardArrowRight } from '@mui/icons-material';
import ReceiptIcon from '@mui/icons-material/Receipt';
import Card from '@mui/material/Card';

// yup
import * as Yup from 'yup';
import { yupResolver } from '@hookform/resolvers/yup';
import { FormProvider, RHFTextField, RHFSelect } from 'components/hook-form';

// redux
import Countries from 'utils/Countries';
import { creatingTaxes, reseTaxesStatus } from 'redux/slices/taxes/taxesSlice';

import useLocales from 'hooks/useLocales';
import Footer from '../Footer';

//  ________________styles_________________
const RootStyle = styled('div')(({ theme }) => ({
  padding: theme.spacing(6, 6),
  width: '100%',
  [theme.breakpoints.down('md')]: {
    padding: theme.spacing(6, 3),
  },
  [theme.breakpoints.down('sm')]: {
    padding: theme.spacing(10, 3),
  },
}));

const CreateTax = () => {
  const { enqueueSnackbar } = useSnackbar();
  const dispatch = useDispatch();
  const theme = useTheme();

  const { translate } = useLocales();
  const valuetype = [
    {
      value: 'vat',
      label: 'vat',
    },
    {
      value: 'sales_tax',
      label: 'sales_tax',
    },
  ];

  const type = [
    {
      value: translate('edit_taxes.inclusive'),
      label: translate('edit_taxes.inclusive'),
    },
    {
      value: translate('edit_taxes.exclusive'),
      label: translate('edit_taxes.exclusive'),
    },
  ];

  const billing = [
    {
      value: translate('edit_taxes.personal_only'),
      label: translate('edit_taxes.personal_only'),
    },
    {
      value: translate('edit_taxes.business_only'),
      label: translate('edit_taxes.business_only'),
    },
    {
      value: translate('edit_taxes.personal_business'),
      label: translate('edit_taxes.personal_business'),
    },
  ];

  const heading = [
    {
      value: translate('name'),
    },
    {
      value: translate('edit_taxes.description'),
    },
  ];
  const [countryName, setCountryName] = React.useState([]);
  const handleChangeMultiple = (event) => {
    const { options } = event.target;
    const value = [];
    for (let i = 0, l = options.length; i < l; i += 1) {
      if (options[i].selected) {
        value.push( [i].value);
      }
    } 
    setCountryName(value);
  };

  const [state, setState] = useState({
    name: '',
    description: '',
    jurisdiction: '',
    percentage: '',
    inclusive: '',
    countryName: '',
    billingInfo: '',
    percent: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setState((prevState) => ({
      ...prevState, 
      [name]: value, 
    })); 
  };

  const handlleSubmit = (e) => {
    e.preventDefault();
    const taxesData = {
      state,
    };

    dispatch(creatingTaxes(taxesData)).then(() => {
      setState({
        name: '',
        description: '',
        jurisdiction: '',
        percentage: '',
        inclusive: '',
        billingInfo: '',
        countryName: '',
        percent: '',
      });
    });
  };

  const { status, message } = useSelector((store) => store?.taxes?.taxCreateState);
  const { taxesIsSuccess, taxesIsLoading, taxesIsError } = useSelector((store) => store?.taxes);

  useEffect(() => {
    if (taxesIsSuccess && status === 'success') {
      enqueueSnackbar(message, { variant: 'success' });
    }
    if (taxesIsSuccess && status === 'failed') {
      // if (status === 'failed') {
      enqueueSnackbar(message, { variant: 'error' });
    }

    dispatch(reseTaxesStatus());
  }, [taxesIsSuccess, taxesIsError]);

  const CreateTaxSchema = Yup.object().shape({
    name: Yup.string().required(translate('required_field')),
    description: Yup.string().required(translate('required_field')),

    jurisdiction: Yup.number()
      .transform((value, original) => (original == null || original === '' ? undefined : value))
      .required(translate('required_field'))
      .min(1, translate('descriptions.one_or_more'))
      .max(100, translate('descriptions.hundred_or_less')),
    // jurisdiction: Yup.string().required("Tax value required").matches(/^[0-9]+$/, 'Must be only numeric valur').min(1,"Enter a numeric value between 1 and 100.").max(100,"Enter a numeric value between 1 and 100."),
    percentage: Yup.string().required(translate('required_field')),
    inclusive: Yup.string().required(translate('required_field')),
    billingInfo: Yup.string().required(translate('required_field')),
    country: Yup.string().required(translate('required_field')),

    percent: Yup.number()
      .transform((value, original) => (original == null || original === '' ? undefined : value))
      .required(translate('required_field'))
      .min(1, translate('descriptions.one_or_more'))
      .max(100, translate('descriptions.hundred_or_less')),
  });
  const defaultValues = {
    name: '',
    description: '',
    jurisdiction: '',
    percentage: '',
    inclusive: '',
    country: '',
    billingInfo: '',
    percent: '',
  };

  const methods = useForm({
    resolver: yupResolver(CreateTaxSchema),
    defaultValues,
  });

  const [subLoader, setSubLoader] = useState(false);

  const submitHandler = (con) => {
    setSubLoader(true);
    ReactGA.event({
      action: 'createtax_action',
      category: 'crreatetax_category',
      label: 'createtax_label',
      value: 'xxxxxx',
    });
    const taxesData = {
      ...con,
    };
    dispatch(creatingTaxes(taxesData));
    setSubLoader(false);
    reset();
  };
  const {
    reset,
    handleSubmit,
    formState: { isSubmitting },
  } = methods;
  // for percenatge

  return (
    <RootStyle>
      <Stack direction="row" justifyContent="start" alignItems="center" mb={2}>
        <Link to="/admin/taxes" component={RouterLink}>
          {translate('edit_taxes.taxes')}{' '}
        </Link>

        <KeyboardArrowRight fontSize="small" />
        <Typography variant="span" text-muted>
          {translate('create_tax')}
        </Typography>
      </Stack>
      <Box sx={{ marginBottom: '1.5rem', display: 'flex', justifyContent: 'space-between !important' }}>
        <Stack direction="row" spacing={1} sx={{ justifyContent: 'start', alignItems: 'center' }}>
          <ReceiptIcon size={24} />

          <Typography variant="h3" ml={2} sx={{ fontWeight: '500' }}>
            {translate('edit_taxes.create_new')}
          </Typography>
        </Stack>
      </Box>
      <Card sx={{ p: { xs: '1.5rem 1rem', sm: '1.5rem 1rem', md: '2rem' } }}>
        <Box
          component="div"
          sx={{
            marginBottom: '1rem',
            color: theme.palette.mode === 'dark' ? '#86cfda' : '#0c5460',
            backgroundColor: theme.palette.mode === 'dark' ? '#143036' : '#d1ecf1',
            borderColor: theme.palette.mode === 'dark' ? '#143c43' : '#bee5eb',
            position: 'relative',
            padding: '0.75rem 1.25rem',
            border: '1px solid transparent',
            borderRadius: '0.25rem',
          }}
        >
          <Typography>{translate('edit_taxes.created_tax')}</Typography>
        </Box>
        <Box
          sx={{
            width: '100%',
            // padding: '1.25rem',
            minWidth: '0',
            wordWrap: 'break-word',
            backgroundClip: 'border-box',
            borderRadius: '0.25rem',
          }}
        >
          <FormProvider methods={methods} onSubmit={handleSubmit(submitHandler)}>
            {/* {heading.map((title) => ( */}
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('name')}</Typography>
              <RHFTextField fullWidth type="text" size="small" name="name" />
            </Box>

            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography>{translate('edit_taxes.description')}</Typography>
              <RHFTextField fullWidth type="text" size="small" name="description" />
            </Box>
            {/* ))} */}
            <Box component="div" sx={{ marginBottom: '1.5rem', display: 'flex' }}>
              <Box sx={{ width: '50%' }}>
                <Typography> {translate('edit_taxes.tax_value')}</Typography>
                <RHFTextField
                  fullWidth
                  type="number"
                  size="small"
                  name="jurisdiction"
                  // value={state?.jurisdiction}
                  // onChange={handleChange}
                  // InputProps={{
                  //   inputProps: {
                  //     max: 100.0,
                  //     min: 1,
                  //   },
                  // }}
                  // helperText="enter a numeric value"
                  onKeyPress={(e) => {
                    if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                      e.preventDefault();
                    }
                  }}
                  onKeyDown={(e) => {
                    if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                      e.preventDefault();
                    }
                  }}
                />
              </Box>
              <Box sx={{ paddingLeft: '15px', width: '50%' }}>
                <Typography> {translate('edit_taxes.value_type')} </Typography>
                <RHFTextField fullWidth select type="text" size="small" name="percentage">
                  {valuetype.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </RHFTextField>
              </Box>
            </Box>
            <Box sx={{ width: '100%', marginBottom: '1.5rem' }}>
              <Typography> {translate('edit_taxes.percentage')}</Typography>
              <RHFTextField
                fullWidth
                type="number"
                size="small"
                name="percent"
                // value={state?.percent}
                // InputProps={{
                //   inputProps: {
                //     max: 100.0,
                //     min: 1,
                //   },
                // }}
                // onChange={(e) => {
                //   let value = parseInt(e.target.value, 10);

                //   if (value > max) value = max;
                //   if (value < min) value = min;

                //   setState((prevState) => ({
                //     ...prevState,
                //     percent: value,
                //   }));
                // }}
                // helperText="enter a numeric value between 0 and 100"
                onKeyPress={(e) => {
                  if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                    e.preventDefault();
                  }
                }}
                onKeyDown={(e) => {
                  if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                    e.preventDefault();
                  }
                }}
              />
            </Box>
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('type')} </Typography>
              <RHFTextField
                select
                multiple
                fullWidth
                type="text"
                size="small"
                name="inclusive"
                // value={state?.inclusive}
                // onChange={handleChange}
              >
                {type.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </RHFTextField>
            </Box>
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('edit_taxes.billing_for')} </Typography>

              {/* <RHFSelect 
                  fullWidth
                  type="text"
                  size="small"
                  name="billingInfo"
                  >
                 {billing.map((option) => (
                  <option key={option.value} value={option.value}>
                  {option.label}
                  </option>
              ))}
              </RHFSelect> */}
              <RHFTextField select multiple fullWidth type="text" size="small" name="billingInfo">
                {billing.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </RHFTextField>
            </Box>

            <Box sx={{ marginBottom: '1.5rem' }}>
              <Countries />

              <Typography disabled sx={{ marginTop: '0.25rem', fontSize: '80%', fontWeight: '400', color: '#728197' }}>
                {translate('edit_taxes.select')}
              </Typography>
            </Box>
            <Box>
              {/* <LoadingButton
                type="submit"
                variant="contained"
                color="info"
               
                sx={{ display: 'block', fontSize: '1rem', fontWeight: '400', padding: '0.375rem 0.75rem' }}
              >
              
              </LoadingButton> */}
              <LoadingButton
                type="submit"
                variant="contained"
                loading={subLoader}
                sx={{ display: 'block', width: '100%', fontSize: '1rem' }}
              >
                {translate('create')}
              </LoadingButton>
            </Box>
          </FormProvider>
        </Box>
      </Card>

      <Footer />
    </RootStyle>
  );
};

export default CreateTax;
