import React,{useState} from 'react';


//  mui
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Link from '@mui/material/Link';
import MenuItem from '@mui/material/MenuItem';
import Stack from '@mui/material/Stack';
import { styled } from '@mui/material/styles';
import Typography from '@mui/material/Typography';
import LoadingButton from '@mui/lab/LoadingButton';
import { KeyboardArrowRight } from '@mui/icons-material';
import ReceiptIcon from '@mui/icons-material/Receipt';
import Card from '@mui/material/Card';





//  ________________styles_________________
const RootStyle = styled('div')(({ theme }) => ({
  padding: theme.spacing(6, 6),
  width: '100%',
  [theme.breakpoints.down('md')]: {
    padding: theme.spacing(6, 3),
  },
  [theme.breakpoints.down('sm')]: {
    padding: theme.spacing(10, 3),
  },
}));

const CreateTax = () => {
 
  const valuetype = [
    {
      value: 'vat',
      label: 'vat',
    },
    {
      value: 'sales_tax',
      label: 'sales_tax',
    },
  ];

  const type = [
    {
      value: 'e',
      label: 'editss',
    },
    {
      value: 'sive',
      label:'exclusive',
    },
  ];

  const billing = [
    {
      value: 'nly',
      label: 'ed',
    },
    {
      value: 'only',
      label: 'edit',
    },
    {
      value: 'persona',
      label: 'business',
    },
  ];

  const heading = [
    {
      value:'name',
    },
    {
      value:'edit taxex',
    },
  ];





  const handlleSubmit = (e) => {
    e.preventDefault();
    const taxesData = {
      state,
    };


  
  };


  const defaultValues = {
    name: '',
    description: '',
    jurisdiction: '',
    percentage: '',
    inclusive: '',
    country: '',
    billingInfo: '',
    percent: '',
  };


  return (
    <RootStyle>
      <Stack direction="row" justifyContent="start" alignItems="center" mb={2}>
        <Link to="/admin/taxes" component={RouterLink}>
          {translate('edit_taxes.taxes')}{' '}
        </Link>

        <KeyboardArrowRight fontSize="small" />
        <Typography variant="span" text-muted>
          {translate('create_tax')}
        </Typography>
      </Stack>
      <Box sx={{ marginBottom: '1.5rem', display: 'flex', justifyContent: 'space-between !important' }}>
        <Stack direction="row" spacing={1} sx={{ justifyContent: 'start', alignItems: 'center' }}>
          <ReceiptIcon size={24} />

          <Typography variant="h3" ml={2} sx={{ fontWeight: '500' }}>
            {translate('edit_taxes.create_new')}
          </Typography>
        </Stack>
      </Box>
      <Card sx={{ p: { xs: '1.5rem 1rem', sm: '1.5rem 1rem', md: '2rem' } }}>
        <Box
          component="div"
          sx={{
            marginBottom: '1rem',
            color: theme.palette.mode === 'dark' ? '#86cfda' : '#0c5460',
            backgroundColor: theme.palette.mode === 'dark' ? '#143036' : '#d1ecf1',
            borderColor: theme.palette.mode === 'dark' ? '#143c43' : '#bee5eb',
            position: 'relative',
            padding: '0.75rem 1.25rem',
            border: '1px solid transparent',
            borderRadius: '0.25rem',
          }}
        >
          <Typography>{translate('edit_taxes.created_tax')}</Typography>
        </Box>
        <Box
          sx={{
            width: '100%',
            // padding: '1.25rem',
            minWidth: '0',
            wordWrap: 'break-word',
            backgroundClip: 'border-box',
            borderRadius: '0.25rem',
          }}
        >
          <FormProvider methods={methods} onSubmit={handleSubmit(submitHandler)}>
            {/* {heading.map((title) => ( */}
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('name')}</Typography>
              <RHFTextField fullWidth type="text" size="small" name="name" />
            </Box>

            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography>{translate('edit_taxes.description')}</Typography>
              <RHFTextField fullWidth type="text" size="small" name="description" />
            </Box>
            {/* ))} */}
            <Box component="div" sx={{ marginBottom: '1.5rem', display: 'flex' }}>
              <Box sx={{ width: '50%' }}>
                <Typography> {translate('edit_taxes.tax_value')}</Typography>
                <RHFTextField
                  fullWidth
                  type="number"
                  size="small"
                  name="jurisdiction"
                  // value={state?.jurisdiction}
                  // onChange={handleChange}
                  // InputProps={{
                  //   inputProps: {
                  //     max: 100.0,
                  //     min: 1,
                  //   },
                  // }}
                  // helperText="enter a numeric value"
                  onKeyPress={(e) => {
                    if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                      e.preventDefault();
                    }
                  }}
                  onKeyDown={(e) => {
                    if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                      e.preventDefault();
                    }
                  }}
                />
              </Box>
              <Box sx={{ paddingLeft: '15px', width: '50%' }}>
                <Typography> {translate('edit_taxes.value_type')} </Typography>
                <RHFTextField fullWidth select type="text" size="small" name="percentage">
                  {valuetype.map((option) => (
                    <MenuItem key={option.value} value={option.value}>
                      {option.label}
                    </MenuItem>
                  ))}
                </RHFTextField>
              </Box>
            </Box>
            <Box sx={{ width: '100%', marginBottom: '1.5rem' }}>
              <Typography> {translate('edit_taxes.percentage')}</Typography>
              <RHFTextField
                fullWidth
                type="number"
                size="small"
                name="percent"
                // value={state?.percent}
                // InputProps={{
                //   inputProps: {
                //     max: 100.0,
                //     min: 1,
                //   },
                // }}
                // onChange={(e) => {
                //   let value = parseInt(e.target.value, 10);

                //   if (value > max) value = max;
                //   if (value < min) value = min;

                //   setState((prevState) => ({
                //     ...prevState,
                //     percent: value,
                //   }));
                // }}
                // helperText="enter a numeric value between 0 and 100"
                onKeyPress={(e) => {
                  if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                    e.preventDefault();
                  }
                }}
                onKeyDown={(e) => {
                  if (e.key === 'e' || e.key === 'E' || e.key === '-' || e.key === '+') {
                    e.preventDefault();
                  }
                }}
              />
            </Box>
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('type')} </Typography>
              <RHFTextField
                select
                multiple
                fullWidth
                type="text"
                size="small"
                name="inclusive"
                // value={state?.inclusive}
                // onChange={handleChange}
              >
                {type.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </RHFTextField>
            </Box>
            <Box sx={{ marginBottom: '1.5rem' }}>
              <Typography> {translate('edit_taxes.billing_for')} </Typography>

              {/* <RHFSelect 
                  fullWidth
                  type="text"
                  size="small"
                  name="billingInfo"
                  >
                 {billing.map((option) => (
                  <option key={option.value} value={option.value}>
                  {option.label}
                  </option>
              ))}
              </RHFSelect> */}
              <RHFTextField select multiple fullWidth type="text" size="small" name="billingInfo">
                {billing.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </RHFTextField>
            </Box>

            <Box sx={{ marginBottom: '1.5rem' }}>
              <Countries />

              <Typography disabled sx={{ marginTop: '0.25rem', fontSize: '80%', fontWeight: '400', color: '#728197' }}>
                {translate('edit_taxes.select')}
              </Typography>
            </Box>
            <Box>
              {/* <LoadingButton
                type="submit"
                variant="contained"
                color="info"
               
                sx={{ display: 'block', fontSize: '1rem', fontWeight: '400', padding: '0.375rem 0.75rem' }}
              >
              
              </LoadingButton> */}
              <LoadingButton
                type="submit"
                variant="contained"
                loading={subLoader}
